import java.io.IOException;import java.util.Scanner;/**   A driver that demonstrates the class Postfix.      @author Frank M. Carrano   @author Timothy M. Henry   @version 4.1*/public class Driver{	public static void main(String[] args) throws IOException	{		java.io.File file = new java.io.File("p4in.txt");		Scanner input = new Scanner(file);		java.io.PrintWriter output = new java.io.PrintWriter("p4out.txt");		output.println("Symbol Table\n");		output.println("Variable\tValue");		output.println("a\t\t\t2\n" +                "b\t\t\t3\n" +                "c\t\t\t4\n" +                "d\t\t\t5\n" +                "e\t\t\t6\n" +                "f\t\t\t0\n" +                "g\t\t\t1\n" +                "h\t\t\t8\n");		output.println("Input\t\t\t\t\tInfix\t\t\tPostfix\t\t\tResult");		while (input.hasNext()) {			String eqInput = input.nextLine();			String[] infixSplit=eqInput.split(" ");			String infixPrevar="";			for (String sp:infixSplit) {				infixPrevar+=sp;			}		    String infix="";		    for (int i=0;i<infixPrevar.length();i++){                char next=infixPrevar.charAt(i);                switch (next){                    case '2': case '3': case '4': case '5': case '6': case '0': case '1': case '8':                        infix+=numToVar(next);                        break;                    case '(': case ')': case '+': case '-': case '*': case '/': case '^':                        infix+=infixPrevar.charAt(i);                        break;                }            }            String postfix=Postfix.convertToPostfix(infix);            double result=Postfix.evaluatePostfix(postfix);            output.printf("%-15s\t\t\t%-15s\t%-15s\t\t%-15s\n",eqInput,infix,postfix,result);		}		output.close();	}    private static char numToVar(char num)    {        switch (num)        {            case '2': return 'a';            case '3': return 'b';            case '4': return 'c';            case '5': return 'd';            case '6': return 'e';            case '0': return 'f';            case '1': return 'g';            case '8': return 'h';        }        return 0;    }}